import React, { useContext, useEffect, useState } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const CITIES = [
  "Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore",
  "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "Varanasi", "Jaipur"
];

const LandingPage = () => {
  const [error, setError] = useState('');
  const [returnTrip, setReturnTrip] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);

  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
  }, []);

  const validateInputs = () => {
    const today = new Date();
    const depDate = new Date(departureDate);
    const retDate = new Date(returnDate);

    if (!departure || !destination || !departureDate || (returnTrip && !returnDate)) {
      return "Please fill all the inputs";
    }

    if (departure === destination) {
      return "Departure and Destination cities must be different";
    }

    if (depDate < today) {
      return "Departure date must be today or later";
    }

    if (returnTrip && retDate <= depDate) {
      return "Return date must be after departure date";
    }

    return null;
  };

  const fetchFlights = async () => {
    const validationMessage = validateInputs();
    if (validationMessage) {
      setError(validationMessage);
      return;
    }

    try {
      setError('');
      const res = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(res.data);
    } catch (err) {
      setError('Failed to fetch flights. Please try again later.');
    }
  };

  const handleBooking = (flightId, origin) => {
    const travelDate = origin === departure ? departureDate : returnDate;
    setTicketBookingDate(travelDate);

    if (userId) {
      navigate(`/book-flight/${flightId}`);
    } else {
      navigate('/auth');
    }
  };

  const filteredFlights = flights.filter(flight => {
    if (returnTrip) {
      return (
        (flight.origin === departure && flight.destination === destination) ||
        (flight.origin === destination && flight.destination === departure)
      );
    } else {
      return flight.origin === departure && flight.destination === destination;
    }
  });

  return (
    <div className="landingPage container py-4">

      {/* Hero Section */}
      <section className="landingHero text-center mb-5">
        <h1 className="banner-h1 mb-3">Embark on an Extraordinary Flight Booking Adventure!</h1>
        <p className="banner-p">Book unforgettable journeys and explore new destinations with ease.</p>
      </section>

      {/* Search Form */}
      <section className="Flight-search-container input-container mb-4 p-4 border rounded shadow-sm">
        <div className="form-check form-switch mb-3">
          <input
            className="form-check-input"
            type="checkbox"
            id="returnSwitch"
            onChange={(e) => setReturnTrip(e.target.checked)}
          />
          <label className="form-check-label" htmlFor="returnSwitch">Return journey</label>
        </div>

        <div className="row g-3 align-items-end">
          <div className="col-md-3">
            <label className="form-label">Departure City</label>
            <select className="form-select" value={departure} onChange={(e) => setDeparture(e.target.value)}>
              <option value="" disabled>Select</option>
              {CITIES.map(city => <option key={city} value={city}>{city}</option>)}
            </select>
          </div>

          <div className="col-md-3">
            <label className="form-label">Destination City</label>
            <select className="form-select" value={destination} onChange={(e) => setDestination(e.target.value)}>
              <option value="" disabled>Select</option>
              {CITIES.map(city => <option key={city} value={city}>{city}</option>)}
            </select>
          </div>

          <div className="col-md-2">
            <label className="form-label">Journey Date</label>
            <input type="date" className="form-control" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} />
          </div>

          {returnTrip && (
            <div className="col-md-2">
              <label className="form-label">Return Date</label>
              <input type="date" className="form-control" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
            </div>
          )}

          <div className="col-md-2">
            <button className="btn btn-primary w-100" onClick={fetchFlights}>Search</button>
          </div>
        </div>

        {error && <p className="text-danger mt-3">{error}</p>}
      </section>

      {/* Flight Results */}
      <section className="my-5">
        {flights.length > 0 ? (
          filteredFlights.length > 0 ? (
            <div className="availableFlightsContainer">
              <h2 className="mb-4">Available Flights</h2>
              <div className="row g-4">
                {filteredFlights.map(flight => (
                  <div className="col-md-6 col-lg-4" key={flight._id}>
                    <div className="card h-100 shadow-sm">
                      <div className="card-body">
                        <h5 className="card-title">{flight.flightName}</h5>
                        <p><strong>Flight Number:</strong> {flight.flightId}</p>
                        <p><strong>Start:</strong> {flight.origin} | <strong>Departure:</strong> {flight.departureTime}</p>
                        <p><strong>Destination:</strong> {flight.destination} | <strong>Arrival:</strong> {flight.arrivalTime}</p>
                        <p><strong>Price:</strong> ₹{flight.basePrice}</p>
                        <p><strong>Seats:</strong> {flight.totalSeats}</p>
                        <button
                          className="btn btn-primary w-100"
                          onClick={() => handleBooking(flight._id, flight.origin)}
                        >
                          Book Now
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="text-center">
              <h4>No flights match your criteria</h4>
            </div>
          )
        ) : null}
      </section>

      {/* About Section */}
      <section id="about" className="section-about p-4 mt-5 bg-light rounded shadow-sm">
        <h2 className="section-title">About Us</h2>
        <p>Welcome to SB FlightConnect – your one-stop platform for seamless flight bookings. Whether it’s for work or wanderlust, we’ve got you covered with trusted operators and competitive fares.</p>
        <p>Customize your travel preferences, compare options, and enjoy hassle-free bookings at your fingertips.</p>
        <p>Start your journey with us today – safe, secure, and seamless.</p>
        <h5 className="mt-4 text-center">2023 SB FlightConnect - &copy; All rights reserved</h5>
      </section>
    </div>
  );
};

export default LandingPage;
