import React, { useContext, useEffect, useState } from 'react'
import '../styles/LandingPage.css'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState();
  const [returnDate, setReturnDate] = useState();

  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
  }, []);

  const [Flights, setFlights] = useState([]);

  const fetchFlights = async () => {
    const date = new Date();
    const date1 = new Date(departureDate);
    const date2 = new Date(returnDate);

    if (checkBox) {
      if (departure && destination && departureDate && returnDate) {
        if (date1 > date && date2 > date1) {
          setError('');
          const res = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(res.data);
        } else setError("Please check the dates");
      } else setError("Please fill all the inputs");
    } else {
      if (departure && destination && departureDate) {
        if (date1 >= date) {
          setError('');
          const res = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(res.data);
        } else setError("Please check the dates");
      } else setError("Please fill all the inputs");
    }
  };

  const handleTicketBooking = (id, origin, destination) => {
    if (userId) {
      setTicketBookingDate(origin === departure ? departureDate : returnDate);
      navigate(`/book-flight/${id}`);
    } else navigate('/auth');
  };

  return (
    <div className="landingPage container py-4">

      {/* Hero Section */}
      <div className="landingHero text-center mb-5">
        <div className="landingHero-title mb-3">
          <h1 className="banner-h1 mb-3">Embark on an Extraordinary Flight Booking Adventure!</h1>
          <p className="banner-p">Unleash your travel desires and book extraordinary Flight journeys that will transport you to unforgettable destinations.</p>
        </div>
      </div>

      {/* Search Form */}
      <div className="Flight-search-container input-container mb-4 p-4 border rounded shadow-sm">
        <div className="form-check form-switch mb-3">
          <input className="form-check-input" type="checkbox" id="flexSwitchCheckDefault" onChange={(e) => setCheckBox(e.target.checked)} />
          <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Return journey</label>
        </div>

        <div className="row g-3 align-items-end">
          <div className="col-md-3">
            <label className="form-label">Departure City</label>
            <select className="form-select" value={departure} onChange={(e) => setDeparture(e.target.value)}>
              <option value="" disabled>Select</option>
              {["Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "varanasi", "Jaipur"].map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label className="form-label">Destination City</label>
            <select className="form-select" value={destination} onChange={(e) => setDestination(e.target.value)}>
              <option value="" disabled>Select</option>
              {["Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "varanasi", "Jaipur"].map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </div>
          <div className="col-md-2">
            <label className="form-label">Journey Date</label>
            <input type="date" className="form-control" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} />
          </div>
          {checkBox && (
            <div className="col-md-2">
              <label className="form-label">Return Date</label>
              <input type="date" className="form-control" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
            </div>
          )}
          <div className="col-md-2">
            <button className="btn btn-primary w-100" onClick={fetchFlights}>Search</button>
          </div>
        </div>
        <p className="text-danger mt-3">{error}</p>
      </div>

      {/* Flight Results */}
      {Flights.length > 0 && (
        Flights.filter(f => f.origin === departure && f.destination === destination).length > 0 || checkBox ?
          <div className="availableFlightsContainer my-5">
            <h2 className="mb-4">Available Flights</h2>
            <div className="row g-4">
              {(checkBox
                ? Flights.filter(f => (f.origin === departure && f.destination === destination) || (f.origin === destination && f.destination === departure))
                : Flights.filter(f => f.origin === departure && f.destination === destination)
              ).map(Flight => (
                <div className="col-md-6 col-lg-4" key={Flight._id}>
                  <div className="card h-100 shadow-sm">
                    <div className="card-body">
                      <h5 className="card-title">{Flight.flightName}</h5>
                      <p><strong>Flight Number:</strong> {Flight.flightId}</p>
                      <p><strong>Start:</strong> {Flight.origin} | <strong>Departure:</strong> {Flight.departureTime}</p>
                      <p><strong>Destination:</strong> {Flight.destination} | <strong>Arrival:</strong> {Flight.arrivalTime}</p>
                      <p><strong>Price:</strong> ₹{Flight.basePrice}</p>
                      <p><strong>Seats:</strong> {Flight.totalSeats}</p>
                      <button className="btn btn-primary w-100" onClick={() => handleTicketBooking(Flight._id, Flight.origin, Flight.destination)}>Book Now</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          :
          <div className="text-center my-5">
            <h2>No Flights Available</h2>
          </div>
      )}

      {/* About Section */}
      <section id="about" className="section-about p-4 mt-5 bg-light rounded shadow-sm">
        <div className="container">
          <h2 className="section-title">About Us</h2>
          <p className="section-description">
            Welcome to our Flight ticket booking app, where we provide exceptional travel experiences from start to finish. Whether you're commuting, exploring, or vacationing, we offer an extensive selection of options.
          </p>
          <p className="section-description">
            Our intuitive interface makes it easy to browse schedules, compare fares, and select the best seating. Book tickets in just a few taps and customize preferences like times, seats, or special needs.
          </p>
          <p className="section-description">
            Discover new destinations, enjoy the journey, and create lasting memories with our reliable, comfortable booking platform. Let us help bring your travel dreams to life!
          </p>
          <h5 className="mt-4 text-center">2023 SB FlightConnect - &copy; All rights reserved</h5>
        </div>
      </section>
    </div>
  );
}

export default LandingPage;
