import React, { useState } from 'react';

const Login = ({ setIsLogin, onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    try {
      // Here you would typically call your authentication function
      // Since we removed the context dependency, we use a prop instead
      if (onLogin) {
        await onLogin(email, password);
      } else {
        // For demo purposes, simulate login delay
        await new Promise(resolve => setTimeout(resolve, 1000));
        console.log("Login attempted with:", { email, password });
      }
    } catch (error) {
      console.error("Login failed:", error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-50">
      <form className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md transition-all duration-500 ease-in-out transform hover:shadow-xl">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800 animate-fade-in">
          Welcome Back
        </h2>
        
        <div className="space-y-4">
          <div className="relative transition-all duration-300 ease-in-out transform hover:translate-y-1">
            <input
              type="email"
              className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300"
              id="email"
              placeholder="Email address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <label 
              htmlFor="email" 
              className="absolute left-4 -top-2.5 bg-white px-1 text-sm text-gray-600"
            >
              Email address
            </label>
          </div>

          <div className="relative transition-all duration-300 ease-in-out transform hover:translate-y-1">
            <input
              type="password"
              className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300"
              id="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <label 
              htmlFor="password" 
              className="absolute left-4 -top-2.5 bg-white px-1 text-sm text-gray-600"
            >
              Password
            </label>
          </div>
        </div>

        <button
          type="submit"
          className={`w-full mt-6 py-3 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700 transition-all duration-300 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 ${
            isLoading ? 'opacity-75 cursor-not-allowed' : ''
          }`}
          onClick={handleLogin}
          disabled={isLoading}
        >
          {isLoading ? (
            <span className="inline-flex items-center">
              <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Signing in...
            </span>
          ) : (
            'Sign in'
          )}
        </button>

        <div className="mt-6 text-center text-gray-600">
          <p>
            Not registered?{' '}
            <span
              onClick={() => setIsLogin(false)}
              className="text-blue-600 cursor-pointer hover:text-blue-800 transition-colors duration-300 font-medium hover:underline"
            >
              Create an account
            </span>
          </p>
        </div>

        <style jsx>{`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(10px); }
            to { opacity: 1; transform: translateY(0); }
          }
          .animate-fade-in {
            animation: fadeIn 0.6s ease-out;
          }
        `}</style>
      </form>
    </div>
  );
};

export default Login;
