import React, { useState } from 'react';

const Login = ({ setIsLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    console.log('Login attempt with:', email, password);
    // In a real application, you would handle authentication here
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <form className="bg-white shadow-md rounded-lg p-8 max-w-md w-full animate-fadeIn">
        <h2 className="text-2xl font-bold text-center mb-6 text-gray-800">Login</h2>
        
        <div className="mb-4 transform transition duration-300 hover:translate-y-1">
          <div className="relative">
            <input
              type="email"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              id="floatingInput"
              placeholder="name@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label
              htmlFor="floatingInput"
              className="absolute left-3 -top-2.5 bg-white px-1 text-sm text-gray-600"
            >
              Email address
            </label>
          </div>
        </div>
        
        <div className="mb-6 transform transition duration-300 hover:translate-y-1">
          <div className="relative">
            <input
              type="password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              id="floatingPassword"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <label
              htmlFor="floatingPassword"
              className="absolute left-3 -top-2.5 bg-white px-1 text-sm text-gray-600"
            >
              Password
            </label>
          </div>
        </div>
        
        <button
          type="submit"
          className="w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded transition duration-300 transform hover:scale-105"
          onClick={handleLogin}
        >
          Sign in
        </button>
        
        <p className="mt-4 text-center text-gray-600">
          Not registered?{' '}
          <span
            onClick={() => setIsLogin(false)}
            className="text-blue-500 cursor-pointer hover:underline"
          >
            Register
          </span>
        </p>
      </form>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.5s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default Login;
