import React, { useContext, useEffect, useState } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const cities = [
  "Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi",
  "Pune", "Trivendrum", "Bhopal", "Kolkata", "Varanasi", "Jaipur"
];

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [Flights, setFlights] = useState([]);

  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem('userType') === 'admin') {
      navigate('/admin');
    } else if (localStorage.getItem('userType') === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, []);

  const fetchFlights = async () => {
    const today = new Date();
    const depDate = new Date(departureDate);
    const retDate = new Date(returnDate);

    if (departure === destination) {
      setError("Departure and destination cities cannot be the same.");
      return;
    }

    if (!departure || !destination || !departureDate || (checkBox && !returnDate)) {
      setError("Please fill all the inputs.");
      return;
    }

    if (depDate < today || (checkBox && retDate <= depDate)) {
      setError("Please check the journey dates.");
      return;
    }

    try {
      const response = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(response.data);
      setError("");
    } catch (err) {
      setError("Error fetching flights. Please try again.");
    }
  };

  const handleTicketBooking = (id, origin) => {
    if (userId) {
      setTicketBookingDate(origin === departure ? departureDate : returnDate);
      navigate(`/book-flight/${id}`);
    } else {
      navigate('/auth');
    }
  };

  return (
    <div className="landingPage">
      <div className="landingHero">
        <div className="landingHero-title">
          <h1 className="banner-h1">Embark on an Extraordinary Flight Booking Adventure!</h1>
          <p className="banner-p">Unleash your travel desires and book extraordinary flight journeys...</p>
        </div>

        <div className="Flight-search-container input-container mb-4">
          <div className="form-check form-switch">
            <input type="checkbox" className="form-check-input" id="flexSwitchCheckDefault" onChange={(e) => setCheckBox(e.target.checked)} />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Return journey</label>
          </div>

          <div className='Flight-search-container-body'>
            <div className="form-floating">
              <select className="form-select form-select-sm mb-3" value={departure} onChange={(e) => setDeparture(e.target.value)}>
                <option value="" disabled>Select</option>
                {cities.map((city) => (
                  <option key={city} value={city} disabled={city === destination}>{city}</option>
                ))}
              </select>
              <label htmlFor="floatingSelect">Departure City</label>
            </div>

            <div className="form-floating">
              <select className="form-select form-select-sm mb-3" value={destination} onChange={(e) => setDestination(e.target.value)}>
                <option value="" disabled>Select</option>
                {cities.map((city) => (
                  <option key={city} value={city} disabled={city === departure}>{city}</option>
                ))}
              </select>
              <label htmlFor="floatingSelect">Destination City</label>
            </div>

            <div className="form-floating mb-3">
              <input type="date" className="form-control" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} />
              <label>Journey date</label>
            </div>

            {checkBox &&
              <div className="form-floating mb-3">
                <input type="date" className="form-control" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
                <label>Return date</label>
              </div>
            }

            <div>
              <button className="btn btn-primary" onClick={fetchFlights}>Search</button>
            </div>

            {error && <p className="text-danger">{error}</p>}
          </div>
        </div>

        {Flights.length > 0 &&
          <div className="availableFlightsContainer">
            <h1>Available Flights</h1>
            <div className="Flights">
              {Flights
                .filter(flight =>
                  checkBox
                    ? (flight.origin === departure && flight.destination === destination) ||
                      (flight.origin === destination && flight.destination === departure)
                    : flight.origin === departure && flight.destination === destination
                )
                .map(flight => (
                  <div className="Flight" key={flight._id}>
                    <div>
                      <p><b>{flight.flightName}</b></p>
                      <p><b>Flight Number:</b> {flight.flightId}</p>
                    </div>
                    <div>
                      <p><b>Start:</b> {flight.origin}</p>
                      <p><b>Departure Time:</b> {flight.departureTime}</p>
                    </div>
                    <div>
                      <p><b>Destination:</b> {flight.destination}</p>
                      <p><b>Arrival Time:</b> {flight.arrivalTime}</p>
                    </div>
                    <div>
                      <p><b>Starting Price:</b> â‚¹{flight.basePrice}</p>
                      <p><b>Available Seats:</b> {flight.totalSeats}</p>
                    </div>
                    <button className="btn btn-primary" onClick={() => handleTicketBooking(flight._id, flight.origin)}>Book Now</button>
                  </div>
                ))
              }
              {
                Flights.filter(flight =>
                  checkBox
                    ? (flight.origin === departure && flight.destination === destination) ||
                      (flight.origin === destination && flight.destination === departure)
                    : flight.origin === departure && flight.destination === destination
                ).length === 0 &&
                <h3>No Flights Found</h3>
              }
            </div>
          </div>
        }

        <section id="about" className="section-about p-4">
          <div className="container">
            <h2 className="section-title">About Us</h2>
            <p className="section-description">Welcome to our Flight ticket booking app...</p>
          </div>
        </section>
      </div>
    </div>
  );
};

export default LandingPage;
