import React, { useContext, useEffect, useState } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const FlightFinder = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [Flights, setFlights] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem('userType') === 'admin') {
      navigate('/admin');
    } else if (localStorage.getItem('userType') === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  const fetchFlights = async () => {
    if (checkBox) {
      if (departure !== "" && destination !== "" && departureDate && returnDate) {
        const date = new Date();
        const date1 = new Date(departureDate);
        const date2 = new Date(returnDate);

        if (date1 < date) {
          setError("Departure date cannot be in the past.");
        } else if (date2 <= date1) {
          setError("Return date must be after the departure date.");
        } else {
          setError("");
          try {
            const response = await axios.get('http://localhost:6001/fetch-flights');
            setFlights(response.data);
          } catch (err) {
            setError("Error fetching flights. Please try again.");
          }
        }
      } else {
        setError("Please fill all the inputs.");
      }
    } else {
      if (departure !== "" && destination !== "" && departureDate) {
        const date = new Date();
        const date1 = new Date(departureDate);

        if (date1 < date) {
          setError("Departure date cannot be in the past.");
        } else {
          setError("");
          try {
            const response = await axios.get('http://localhost:6001/fetch-flights');
            setFlights(response.data);
          } catch (err) {
            setError("Error fetching flights. Please try again.");
          }
        }
      } else {
        setError("Please fill all the inputs.");
      }
    }
  };

  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');

  const handleTicketBooking = async (id, origin, destination) => {
    if (userId) {
      if (origin === departure) {
        setTicketBookingDate(departureDate);
        navigate(`/book-flight/${id}`);
      } else if (destination === departure) {
        setTicketBookingDate(returnDate);
        navigate(`/book-flight/${id}`);
      }
    } else {
      navigate('/auth');
    }
  };

  return (
    <div className="flightFinder">
      <div className="heroSection">
        <h1 className="title">Welcome to Flight Finder</h1>
        <p className="subtitle">
          Find your perfect flight with ease. From domestic to international, we help you take the first step in your journey.
        </p>
      </div>

      <div className="flightSearchContainer">
        <div className="form-check form-switch">
          <input
            className="form-check-input"
            type="checkbox"
            id="returnJourneySwitch"
            value=""
            onChange={(e) => setCheckBox(e.target.checked)}
          />
          <label className="form-check-label" htmlFor="returnJourneySwitch">Return Journey</label>
        </div>

        <div className="form-group">
          <label htmlFor="departureCity">Departure City</label>
          <select
            id="departureCity"
            className="form-select"
            value={departure}
            onChange={(e) => setDeparture(e.target.value)}
          >
            <option value="" disabled>Select Departure City</option>
            <option value="Chennai">Chennai</option>
            <option value="Bangalore">Bangalore</option>
            <option value="Hyderabad">Hyderabad</option>
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
            <option value="Pune">Pune</option>
            <option value="Kolkata">Kolkata</option>
            <option value="Jaipur">Jaipur</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="destinationCity">Destination City</label>
          <select
            id="destinationCity"
            className="form-select"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          >
            <option value="" disabled>Select Destination City</option>
            <option value="Chennai">Chennai</option>
            <option value="Bangalore">Bangalore</option>
            <option value="Hyderabad">Hyderabad</option>
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
            <option value="Pune">Pune</option>
            <option value="Kolkata">Kolkata</option>
            <option value="Jaipur">Jaipur</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="departureDate">Departure Date</label>
          <input
            type="date"
            id="departureDate"
            className="form-control"
            value={departureDate}
            onChange={(e) => setDepartureDate(e.target.value)}
          />
        </div>

        {checkBox && (
          <div className="form-group">
            <label htmlFor="returnDate">Return Date</label>
            <input
              type="date"
              id="returnDate"
              className="form-control"
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
            />
          </div>
        )}

        <div className="searchButtonContainer">
          <button className="btn btn-primary" onClick={fetchFlights}>Search Flights</button>
        </div>

        {error && <p className="error-message">{error}</p>}
      </div>

      {Flights.length > 0 && (
        <div className="availableFlights">
          <h2>Available Flights</h2>
          <div className="flightList">
            {Flights.filter(flight => flight.origin === departure && flight.destination === destination)
              .map(flight => (
                <div className="flightCard" key={flight._id}>
                  <h3>{flight.flightName}</h3>
                  <p><strong>Flight Number:</strong> {flight.flightId}</p>
                  <p><strong>Departure:</strong> {flight.origin} | {flight.departureTime}</p>
                  <p><strong>Arrival:</strong> {flight.destination} | {flight.arrivalTime}</p>
                  <p><strong>Price:</strong> {flight.basePrice} | <strong>Seats Available:</strong> {flight.totalSeats}</p>
                  <button className="btn btn-primary" onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}>
                    Book Now
                  </button>
                </div>
              ))}
          </div>
        </div>
      )}

      <section className="aboutSection">
        <div className="container">
          <h2>About Us</h2>
          <p>Flight Finder connects you to the most convenient and affordable flights across the globe. We focus on simplicity, user-friendliness, and providing an experience that makes your journey smooth from start to finish.</p>
          <p>Our app helps you compare prices, view real-time seat availability, and book flights in a matter of minutes. Whether you're planning a quick business trip or a long vacation, Flight Finder has you covered.</p>
          <p>Join us and start your journey to amazing destinations today!</p>
          <footer>
            <p>&copy; 2023 Flight Finder - All Rights Reserved</p>
          </footer>
        </div>
      </section>
    </div>
  );
};

export default FlightFinder;
